package com.basic;

public class SwitchCaseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number=50;
		switch(number){
		case 10:
		System.out.println("match value is 10");
		break;
		
		case 20:
		System.out.println("match value is 20");
		break;
		
		case 30:
		System.out.println("match the value is 30");
		break;
		
		
		default:
		System.out.println("no match found");
		}
		
	}

}
