
public class ConvertIntoString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=120;
		float f=12.13f;
		double d=2345.0d;
		boolean b=true;
		
		System.out.println("After converting into String  : ");
		
		System.out.println(" String value of int  : "+String.valueOf(i));
		System.out.println(" String value of int  : "+String.valueOf(f));
		System.out.println(" String value of int  : "+String.valueOf(d));
		System.out.println(" String value of int  : "+String.valueOf(b));


	}

}
